package com;

import java.io.ByteArrayInputStream;

import org.bouncycastle.asn1.ASN1InputStream;
import org.bouncycastle.asn1.DLSequence;
import org.hyperledger.common.ByteUtils;

public class TestKey {

	public static void main(String[] args) {
		
		//连接Peer结占为

		byte[] cert = ByteUtils.fromHex("");
		
		try
		{
			ByteArrayInputStream bis1 = new ByteArrayInputStream(cert);

			ASN1InputStream dis1 = new ASN1InputStream(bis1);
		    DLSequence seq1 = (DLSequence) dis1.readObject();
		    System.out.println(seq1.toString());
			    
		}catch(Exception e)
		{
			e.printStackTrace();
		}
		
		

	}

	
}
